mapper.update:
update inquiry
set ANSWER_CONTENT = #{answer_Content}, ANSWER_AT = #{answer_At}
where post_id = #{post_Id}

update community
set DELETED = #{deleted}, DELETED_AT = #{deleted_At}
where post_id = #{post_Id}

