where ID = #{id}

update BOARD_ROOMMATE
set DELETED = #{deleted}, DELETED_AT = #{deleted_At}
where ROOMMATE_ID = #{roommate_Id}

update MONTHLY_PRODUCT
set DELETED_AT = #{deleted_At}
where ID = #{Id}
