mapper.select:
select DISTINCT roomId, receiver, sender
from (
    select DISTINCT
        cr.room_id as roomId, cr.receiver as receiver, cr.sender as sender, cm.create_date as createDate
    from chat_room cr
        inner join chat_message cm
        on cr.room_id = cm.room_id
    order by createDate desc) as sub
where
    sender = #{myid}
    or receiver = #{myid}
order by createDate desc;

select id, user_image as userImage, user_nickname as userNickname
from user_entity
where id = #{tomato}

select lastMessage, lastDateTime from (select message as lastMessage, create_date as lastDateTime, room_id as roomId, ROW_NUMBER() OVER(PARTITION BY room_id ORDER BY create_date DESC) AS rn from chat_message) as sub where rn = 1 and roomId in
#{item.roomId}
order by lastDateTime desc;

select message, createDate, sender from (select room_id as roomId, message, create_date as createDate, sender, ROW_NUMBER() OVER(PARTITION BY room_id ORDER BY create_date DESC) AS rn from chat_message ) as sub where rn = 1 and roomId in
#{item.roomId}
order by createDate desc
