mapper.select:
select id, user_image as userImage, user_nickname as userNickname
from user_entity
where id = #{id}

select message, createDate, sender from (select room_id as roomId, message, create_date as createDate, sender, ROW_NUMBER() OVER(PARTITION BY room_id ORDER BY create_date DESC) AS rn from chat_message ) as sub where rn = 1 and roomId in
#{item.roomId}
order by createDate desc
